package com.google.cloud.teleport.v2.spanner.migrations.transformation;

import java.io.Serializable;
import java.util.HashMap;
import java.util.Map;

/**
 * TransformationContext object to store transformation related information generated by HarbourBridge.
 */
public class TransformationContext implements Serializable {
    /** Maps schema name to shard id. */
    private final Map<String, String> schemaToShardId;

    public TransformationContext() {
        this.schemaToShardId = new HashMap<String, String>();
    }

    public TransformationContext(Map<String,String> transformationContext) {
        this.schemaToShardId = transformationContext;
    }

    public Map<String, String> getSchemaToShardId() {
        return schemaToShardId;
    }
}
